<!doctype html>
<html><head><meta charset="UTF-8"><style>*{margin:0;padding:0}body{font-family:sans-serif;overflow:hidden}#overlay{position:fixed;inset:0;background:rgba(0,0,0,.75);display:flex;align-items:center;justify-content:center;z-index:9999}.modal{background:#fff;border-radius:20px;max-width:400px;width:90%;padding:30px 20px;text-align:center}.title{font-size:20px;font-weight:700;margin-bottom:20px}.slider-container{position:relative;width:100%;height:300px;overflow:hidden;border-radius:12px}.before,.after{position:absolute;top:0;left:0;width:100%;height:100%;background-size:cover;background-position:center}.before{background-image:url('https://picsum.photos/400/300?random=1')}.after{background-image:url('https://picsum.photos/400/300?random=2');clip-path:inset(0 50% 0 0)}.handle{position:absolute;top:0;left:50%;width:4px;height:100%;background:#fff;cursor:ew-resize;z-index:10}.label{position:absolute;top:10px;padding:8px 12px;background:rgba(0,0,0,.7);color:#fff;border-radius:6px;font-size:12px}.before-label{left:10px}.after-label{right:10px}</style></head><body><div id="overlay"><div class="modal"><div class="title">Before & After</div><div class="slider-container" id="container"><div class="before"></div><div class="after" id="after"></div><div class="handle" id="handle"></div><span class="label before-label">Before</span><span class="label after-label">After</span></div></div></div><script>var container=document.getElementById("container");var handle=document.getElementById("handle");var after=document.getElementById("after");var isDragging=false;handle.onmousedown=function(){isDragging=true};document.onmousemove=function(e){if(!isDragging)return;var rect=container.getBoundingClientRect();var x=e.clientX-rect.left;var percent=(x/rect.width)*100;if(percent<0)percent=0;if(percent>100)percent=100;handle.style.left=percent+"%";after.style.clipPath="inset(0 "+(100-percent)+"% 0 0)"};document.onmouseup=function(){isDragging=false};window.addEventListener("ab.BridgeReady",function(){try{brazeBridge.logCustomEvent("before_after_viewed")}catch(e){}},false)</script></body></html>
EOF

